import { toDisplayString } from '../clock';
export class DelOp {
    id;
    obj;
    what;
    constructor(id, obj, what) {
        this.id = id;
        this.obj = obj;
        this.what = what;
    }
    span() {
        return 0;
    }
    name() {
        return 'del';
    }
    toString() {
        const spans = this.what.map((span) => toDisplayString(span) + '!' + span.span).join(', ');
        return `"${this.name()}" ${toDisplayString(this.id)}, obj = ${toDisplayString(this.obj)} { ${spans} }`;
    }
}
